function setTime() {
    let currentDate = new Date();
    $(".time-table-header__clock span").html(
        `${currentDate.getHours()}:${currentDate.getMinutes()}`
    )
    console.log(`${currentDate.getHours()}:${currentDate.getMinutes()}`)
}

let bannersTimeout = +$(".screen-box").attr("data-timeout")
function showHideBanners() {
    $(".screen-box").addClass("_hide-banner")

    setInterval(()=>{
        $(".screen-box").removeClass("_hide-banner")

        setInterval(()=>{
            $(".screen-box").addClass("_hide-banner")
        },bannersTimeout)
    },bannersTimeout*3)
}

setTimeout(()=>{
    showHideBanners()
},bannersTimeout)


function Sliders() {
    let SlidersArray = []

    let sliders = document.querySelectorAll(".slider")
    sliders.forEach((slider,index) => {
        slider.setAttribute("data-slider-id",index)
        let autoplayTimeout = +slider.getAttribute("data-timeout")
        console.log(autoplayTimeout)


        SlidersArray[index] = new Swiper(`.slider[data-slider-id="${index}"] .swiper-container`, {
            slidesPerView: "auto",
            spaceBetween: 0,
            watchSlidesProgress: true,
            preventClicks :true,
            a11y: false,
            observer: true,
            observeParents: true,
            observeSlideChildren: true,
            loop: true,
            autoplay: {
              delay: autoplayTimeout,
              disableOnInteraction: false
            },
            navigation: {
                nextEl: `.slider[data-slider-id="${index}"] .swiper-button-next`,
                prevEl: `.slider[data-slider-id="${index}"] .swiper-button-prev`,
            },
            pagination: {
                el: `.slider[data-slider-id="${index}"] .swiper-pagination`,
                type: 'fraction',
                clickable: true,
                renderFraction: function (currentClass, totalClass) {
                    return 'Страница <span class="' + currentClass + '"></span>' + ' из ' + ' <span class="' + totalClass + '"> </span>';
                    return `
                        Страница <span class="${currentClass}"></span> из <span class="${totalClass}"></span>
                    `
                }
            },
        })
    })
}

function gallery() {
    let SlidersArray = []

    let sliders = document.querySelectorAll(".gallery")
    sliders.forEach((slider,index) => {
        slider.setAttribute("data-slider-id",index)
        let gallery = {
            mySwiper: {},
            mySwiper2: {}
        }

        gallery.mySwiper = new Swiper(`.gallery[data-slider-id="${index}"] .mySwiper`, {
            spaceBetween: 0,
            slidesPerView: "auto",
            freeMode: true,
            watchSlidesProgress: true,
            direction: "vertical",
            breakpoints: {
                320: {
                    direction: "horizontal",
                },
                992: {
                    direction: "vertical",
                }
            }
        });
        gallery.mySwiper2 = new Swiper(`.gallery[data-slider-id="${index}"] .mySwiper2`, {
            spaceBetween: 0,
            direction: "vertical",
            loop: true,
            navigation: {
                nextEl: `.gallery[data-slider-id="${index}"] .swiper-button-next`,
                prevEl: `.gallery[data-slider-id="${index}"] .swiper-button-prev`,
            },
            thumbs: {
                swiper:  gallery.mySwiper,
            },
            breakpoints: {
                320: {
                    direction: "horizontal",
                },
                992: {
                    direction: "vertical",
                }
            }
        });

        SlidersArray[index] = gallery
    })

}


Sliders()
gallery()


